DROP DATABASE IF EXISTS seguimiento_proyectos;
-- -- -----------------------------------------------------------------------------------
CREATE DATABASE seguimiento_proyectos DEFAULT CHARACTER SET latin1 COLLATE latin1_bin;
USE seguimiento_proyectos;
-- ------------------------------------------------------
--  DDL for Table usuarios
-- ------------------------------------------------------
CREATE TABLE usuarios(
	codigo_usuario VARCHAR(30) PRIMARY KEY, 
	nombres_usuario VARCHAR(150), 
	apellidos_usuario VARCHAR(150), 
	nacimiento_usuario DATE, 
	password_usuario VARCHAR(32)
);
-- ------------------------------------------------------
--  DDL for Table proyectos
-- ------------------------------------------------------
CREATE TABLE proyectos(
	codigo_proyecto INT(7) PRIMARY KEY,
	nombre_proyecto VARCHAR(150), 
	descripcion_proyecto VARCHAR(255), 
	inicio_proyecto DATE, 
	fin_proyecto DATE, 
	responsable_proyecto VARCHAR(30)
);
-- ------------------------------------------------------
--  DDL for Table tareas
-- ------------------------------------------------------
CREATE TABLE tareas(
	codigo_tarea INT(7) PRIMARY KEY,
	nombre_tarea VARCHAR(150), 
	descripcion_tarea VARCHAR(255), 
	codigo_proyecto INT(7)
);
-- ------------------------------------------------------
--  DDL for Table responsables
-- ------------------------------------------------------
CREATE TABLE responsables(
	codigo_responsable INT(7) PRIMARY KEY,
	codigo_tarea INT(7),
	codigo_usuario VARCHAR(30)
);





-- ------------------------------------------------------
--  Ref Constraints for Table proyectos
-- ------------------------------------------------------
ALTER TABLE proyectos ADD CONSTRAINT proyectos_usuarios FOREIGN KEY(responsable_proyecto)
REFERENCES usuarios(codigo_usuario) ON DELETE RESTRICT ON UPDATE RESTRICT;
-- ------------------------------------------------------
--  Ref Constraints for Table responsables
-- ------------------------------------------------------
ALTER TABLE responsables ADD CONSTRAINT responsables_tareas FOREIGN KEY(codigo_tarea)
REFERENCES tareas(codigo_tarea) ON DELETE RESTRICT ON UPDATE RESTRICT;

ALTER TABLE responsables ADD CONSTRAINT responsables_usuarios FOREIGN KEY(codigo_usuario)
REFERENCES usuarios(codigo_usuario) ON DELETE RESTRICT ON UPDATE RESTRICT;
-- ------------------------------------------------------
--  Ref Constraints for Table tareas
-- ------------------------------------------------------
ALTER TABLE tareas ADD CONSTRAINT tareas_proyectos FOREIGN KEY(codigo_proyecto)
REFERENCES proyectos(codigo_proyecto) ON DELETE RESTRICT ON UPDATE RESTRICT;